#Requires -RunAsAdministrator

function Enable-FilesystemLongPaths {
    Set-ItemProperty -Path "HKLM:\SYSTEM\CurrentControlSet\Control\FileSystem" -Name "LongPathsEnabled" -Value 1
    Write-Host "FilesystemLongPaths registry is enabled"
}

function Enable-StrongNameVerification {
    reg add HKEY_LOCAL_MACHINE\SOFTWARE\Microsoft\StrongName\Verification\*,31bf3856ad364e35 /v TestPublicKey /t REG_SZ /d  /f
    reg add HKEY_LOCAL_MACHINE\SOFTWARE\Wow6432Node\Microsoft\StrongName\Verification\*,31bf3856ad364e35 /v TestPublicKey /t REG_SZ /d  /f

    Write-Host "StrongNameVerification registry is added"
}

Enable-FilesystemLongPaths
Enable-StrongNameVerification
